mapper.select:
SELECT ID as "id", SI_CD as "regionCd", SI_NM as "regionNm", COORD as "coord", COALESCE(b."cnt",0) AS "cnt", CITY_NM as "cityNm", CITY_LON as "cityLon", CITY_LAT as "cityLat", CITY_SCALE as "cityScale" FROM (SELECT ID ,SI_CD ,SI_NM ,ST_ASTEXT(GEOM) coord ,CITY_NM ,CITY_LAT ,CITY_LON ,CITY_SCALE FROM CM_AREA_SI_CD a WHERE ST_GEOMETRYTYPE(GEOM) LIKE 'ST_MultiPolygon'
AND SI_CD::text = #{sigCode}
ORDER BY ID ASC) a LEFT OUTER JOIN (SELECT ROW_NUMBER() OVER(ORDER BY SUBSTR(AREA_CD,1,5)) AS "rnum", SUBSTR(a.AREA_CD,1,5) AS "areaCd", SUM(CASE WHEN a.AREA_CD = '' THEN 0 ELSE 1 END ) AS "cnt" FROM CM_EVT_OCR a WHERE 1=1
AND TO_CHAR(TO_DATE(EVT_OCR_YMD_HMS, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD') >= #{timeS}
AND #{timeE} >= TO_CHAR(TO_DATE(EVT_OCR_YMD_HMS, 'YYYYMMDDHH24MISS'), 'YYYY-MM-DD')
GROUP BY SUBSTR(AREA_CD,1,5)) b ON a.SI_CD::text=b."areaCd" ORDER BY SI_NM

SELECT ID as "id", SI_CD as "regionCd", SI_NM as "regionNm", COORD as "coord", COALESCE(c."cnt",0) AS "cnt", CITY_NM as "cityNm", CITY_LON as "cityLon", CITY_LAT as "cityLat", CITY_SCALE as "cityScale" FROM ( SELECT ID , SI_CD::text , SI_NM , ST_ASTEXT(GEOM) coord , CITY_NM , CITY_LAT , CITY_LON , CITY_SCALE FROM CM_AREA_SI_CD a WHERE ST_GEOMETRYTYPE(GEOM) LIKE 'ST_MultiPolygon' ORDER BY ID ASC ) a LEFT OUTER JOIN ( SELECT SUM(b."cnt") AS "cnt" ,SUBSTR( b."areaCd" ,1,5) AS "areaCd" FROM( SELECT b."areaCd", SUM(CASE WHEN b.AREA_CD::text = '' THEN 0 ELSE 1 END ) AS "cnt" FROM ( SELECT a.AREA_CD::text AS "areaCd", a.* FROM FACILITY_S a INNER JOIN CCTV_BRK_LOG b ON (a.FCLT_ID = b.FCLT_ID ) WHERE 1=1 AND a.AREA_CD IS NOT NULL
AND TO_CHAR(b.INSERT_DATE, 'yyyy-mm-dd') >= #{timeS}
AND #{timeE} >= TO_CHAR(b.INSERT_DATE, 'yyyy-mm-dd')
) b GROUP BY b."areaCd" ) b GROUP BY SUBSTR(b."areaCd",1,5) ) c ON a.SI_CD=c."areaCd"

SELECT ID as "id", DONG_CD as "regionCd", DONG_NM as "regionNm", COORD as "coord", COALESCE(b."cnt",0) AS "cnt" FROM ( SELECT ID, DONG_CD::text, DONG_NM, ST_ASTEXT(GEOM) coord FROM CM_AREA_DONG_CD a WHERE ST_GEOMETRYTYPE(GEOM) LIKE 'ST_MultiPolygon' AND DONG_CD::text LIKE '%'||#{sigCode}||'%' ) a LEFT OUTER JOIN ( SELECT b."areaCd", SUM(CASE WHEN b.AREA_CD = null THEN 0 ELSE 1 END ) AS "cnt" FROM ( SELECT a.AREA_CD::text AS "areaCd", a.* FROM FACILITY_S a INNER JOIN CCTV_BRK_LOG b ON (a.FCLT_ID = b.FCLT_ID ) WHERE 1=1 AND a.AREA_CD IS NOT NULL
AND TO_CHAR(b.INSERT_DATE, 'yyyy-mm-dd') >= #{timeS}
AND #{timeE} >= TO_CHAR(b.INSERT_DATE, 'yyyy-mm-dd')
) b GROUP BY b."areaCd" ) b ON a.DONG_CD=b."areaCd" ORDER BY a.ID ASC
